import React from 'react';
import { FilterState } from '../model/types';
import { Input } from '../../../shared/ui/Input';
import { Select } from '../../../shared/ui/Select';

interface FilterPanelProps {
  filters: FilterState;
  onFiltersChange: (filters: Partial<FilterState>) => void;
  availableConditions: string[];
}

export const FilterPanel: React.FC<FilterPanelProps> = ({
  filters,
  onFiltersChange,
  availableConditions,
}) => {
  const conditionOptions = [
    { value: '', label: 'Все состояния' },
    ...availableConditions.map(condition => ({
      value: condition,
      label: condition,
    })),
  ];

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-medium text-gray-900 dark:text-white">
        Фильтры и поиск
      </h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Search by SHK */}
        <div>
          <Input
            label="Поиск по ШК"
            type="search"
            value={filters.search}
            onChange={(value) => onFiltersChange({ search: value })}
            placeholder="Введите ШК товара"
          />
        </div>

        {/* Filter by Name */}
        <div>
          <Input
            label="Фильтр по наименованию"
            value={filters.name}
            onChange={(value) => onFiltersChange({ name: value })}
            placeholder="Введите наименование"
          />
        </div>

        {/* Filter by WR Name */}
        <div>
          <Input
            label="Фильтр по наименованию упаковки"
            value={filters.wrName}
            onChange={(value) => onFiltersChange({ wrName: value })}
            placeholder="Введите наименование упаковки"
          />
        </div>

        {/* Filter by Condition */}
        <div>
          <Select
            label="Фильтр по состоянию"
            value={filters.condition}
            onChange={(value) => onFiltersChange({ condition: value })}
            options={conditionOptions}
            placeholder="Выберите состояние"
          />
        </div>
      </div>
    </div>
  );
};
